let ordersController={init(){this.displayOrders(),this.setupSearchListener()},searchOrders(){this.filterOrders()},setupSearchListener(){let r=document.getElementById("searchInput");r.addEventListener("input",()=>{var e=r.value.trim();isNaN(e)&&this.filterOrders()})},filterOrders(){var e=document.getElementById("searchInput").value.trim(),t=document.getElementById("statusFilter").value;if(isNaN(e)||""===e)this.displayOrders(t,e.toLowerCase());else{let r=parseInt(e,10);t=model.orders.filter(e=>e.orderId===r);0<t.length?ordersView.renderOrders(t):ordersView.renderNoResultsMessage("Ingen bestilling funnet med ID "+r)}},displayOrders(r="Alle",t=""){var e=model.orders.filter(e=>("Alle"===r||e.status===r)&&(""===t||e.customerName.toLowerCase().includes(t)));ordersView.renderOrders(e)},resetSearch(){document.getElementById("searchInput").value="",document.getElementById("statusFilter").value="Alle",this.displayOrders()},updateStatus(e,r){model.updateOrderStatus(e,r),this.displayOrders()}};function safeText(e){return DOMPurify.sanitize(e).replace(/</g,"&lt;").replace(/>/g,"&gt;").replace(/&/g,"&amp;").replace(/"/g,"&quot;")}document.addEventListener("DOMContentLoaded",()=>{ordersController.init()});