function getProductsForCategory(e){var t,n=model.categories.indexOf(e),u=[];for(t of model.products)t.categoryIndex===n&&u.push(t);return u}function getProductFromId(t){return model.products.find(e=>e.productId===t)||null}function openProductInfo(e){model.app.selectedProduct=e,updateView()}function closeOverlay(){var e=model.app.selectedProduct;e&&"customizable"===e.type?(model.inputs.themeCakeMenu.size=8,model.inputs.themeCakeMenu.selectedThemeId=1,model.inputs.themeCakeMenu.selectedFlavor="Vanilje",model.inputs.themeCakeMenu.quantity=1):e&&(model.inputs.cakeMenu.size=8,model.inputs.cakeMenu.quantity=1),model.app.selectedProduct=null,model.inputs.cafeMenu.message="",model.inputs.cafeMenu.quantity=1,model.inputs.cakeMenu.size=8,model.inputs.cakeMenu.quantity=1,model.inputs.cakeMenu.message="",updateView()}function getProductsForCurrentPage(){return model.app.currentPageIndex===cafeMenu?getProductsForCurrentTab():model.app.currentPageIndex===cakes?getProductsForCategory("cakes"):void 0}function safeText(e){return DOMPurify.sanitize(e).replace(/</g,"&lt;").replace(/>/g,"&gt;").replace(/&/g,"&amp;").replace(/"/g,"&quot;")}function hamburger(){document.getElementById("hamburger-menu").addEventListener("click",function(){document.querySelector("#header-container").classList.toggle("is-active")}),document.querySelectorAll(".header__nav button").forEach(e=>{e.addEventListener("click",function(){document.querySelector("#header-container").classList.remove("is-active")})})}function formatDateTime(e,t){var[e,n,u]=e.split("-");return u+`-${n}-${e} `+t}